import React from 'react';
import { useLocation } from 'react-router-dom';
import {
  Box,
  Paper,
  Typography,
  Card,
  CardContent,
  Chip,
  Stack,
  CircularProgress,
} from '@mui/material';

interface LocationState {
  query: string;
  queryType: string;
}

const Results: React.FC = () => {
  const location = useLocation();
  const { query, queryType } = (location.state as LocationState) || {};

  // TODO: Implement results fetching logic
  const isLoading = false;
  const results = {
    answer: 'This is a placeholder answer that would be replaced with actual results from the backend.',
    confidence: 0.85,
    sources: [
      {
        title: 'Document 1',
        type: 'text',
        relevance: 0.9,
      },
      {
        title: 'Image 1',
        type: 'image',
        relevance: 0.75,
      },
    ],
  };

  if (!query) {
    return (
      <Box sx={{ textAlign: 'center', mt: 4 }}>
        <Typography variant="h5">No query provided</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ maxWidth: 1000, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" color="textSecondary">
          Query
        </Typography>
        <Typography variant="h6" gutterBottom>
          {query}
        </Typography>
        <Chip
          label={queryType}
          color="primary"
          size="small"
          sx={{ mt: 1 }}
        />
      </Paper>

      {isLoading ? (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="subtitle1" color="textSecondary" gutterBottom>
                Answer
              </Typography>
              <Typography variant="body1" paragraph>
                {results.answer}
              </Typography>
              <Stack direction="row" spacing={1} alignItems="center">
                <Typography variant="subtitle2" color="textSecondary">
                  Confidence:
                </Typography>
                <Chip
                  label={`${(results.confidence * 100).toFixed(1)}%`}
                  color={results.confidence > 0.8 ? 'success' : 'warning'}
                  size="small"
                />
              </Stack>
            </CardContent>
          </Card>

          <Typography variant="h6" gutterBottom>
            Sources
          </Typography>
          <Stack spacing={2}>
            {results.sources.map((source, index) => (
              <Paper key={index} sx={{ p: 2 }}>
                <Stack
                  direction="row"
                  justifyContent="space-between"
                  alignItems="center"
                >
                  <Box>
                    <Typography variant="subtitle1">
                      {source.title}
                    </Typography>
                    <Chip
                      label={source.type}
                      size="small"
                      sx={{ mr: 1 }}
                    />
                    <Chip
                      label={`${(source.relevance * 100).toFixed(1)}% relevant`}
                      color={source.relevance > 0.8 ? 'success' : 'warning'}
                      size="small"
                    />
                  </Box>
                </Stack>
              </Paper>
            ))}
          </Stack>
        </>
      )}
    </Box>
  );
};

export default Results;
